        return ERC20Storage.layout().balanceOf(owner);
    }

    /// @inheritdoc IERC20
    function allowance(address owner, address spender) public view virtual returns (uint256 value) {
        return ERC20Storage.layout().allowance(owner, spender);
    }
}

