    function batchMint(address[] calldata recipients, uint256[] calldata values) external virtual {
        AccessControlStorage.layout().enforceHasRole(MINTER_ROLE, _msgSender());
        ERC20Storage.layout().batchMint(recipients, values);
    }
}

